/* eslint-disable import/no-cycle */
import React from 'react';
import PropTypes from 'prop-types';
import './MovieItem.css';
import { Card, Tag, Rate, Statistic } from 'antd';
import { format } from 'date-fns';
import { GenresContext } from '../App'


function MovieItem({ movie, setMyRatedMovies }) {
  const { Meta } = Card;

  const fallbackImg = 'data:image/png;base64,'


  const truncate = (str, num, useWordBoundary) => {
    if (str.length <= num) { return str; }
    const subString = str.substr(0, num - 1); // the original check
    return useWordBoundary ? `${subString.substr(0, subString.lastIndexOf(" "))}...` : `${subString}...`;
  }

  // eslint-disable-next-line consistent-return
  const getColorRating = (rating) => {
    const colors = {
      red: '#E90000',
      orange: '#E97E00',
      yellow: '#E9D100',
      green: '#66E900',
    }

    if (rating >= 0 && rating < 3) {
      return colors.red
    }
    if (rating >= 3 && rating < 5) {
      return colors.orange
    }
    if (rating >= 5 && rating < 7) {
      return colors.yellow
    }
    if (rating >= 7) {
      return colors.green
    }

  }


  return (

    <Card className="movie-card"

      style={{ width: "100%" }}
      cover={
        movie.poster_path
          ? <img alt="movie preview" src={`https://image.tmdb.org/t/p/w500${movie.poster_path}`} />
          : <img alt="movie preview" src={fallbackImg} />
      }
    >
      <Meta
        title={movie.title}
        style={{
          paddingRight: 55
        }}
      />
      <Statistic
        title={null}
        value={movie.vote_average}
        valueStyle={{
          fontSize: 12,
          width: 30,
          height: 30,
          border: `1px solid ${getColorRating(movie.vote_average)}`,
          borderRadius: '50%',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          position: 'absolute',
          top: 12,
          right: 9,
        }}

      />
      <div className="movie-card__date">
        {/* {movie.release_date} */}
        {movie.release_date ? format(new Date(movie.release_date), 'LLLL d, yyyy') : 'нет данных'}
      </div>
      <div className="movie-card__tags">
        <GenresContext.Consumer>
          {genres => genres.map(genre => movie.genre_ids.includes(genre.id)
            ? <Tag key={genre.id} style={{ marginBottom: 8 }}>{genre.name}</Tag>
            : null)}
        </GenresContext.Consumer>
      </div>
      <p className="movie-card__descr">
        {movie.overview ? truncate(movie.overview, 150, true) : 'No further description'}
      </p>
      <Rate
        allowHalf
        defaultValue={movie.rating}
        count={10}
        style={{ fontSize: 18 }}
        onChange={(value) => setMyRatedMovies(movie.id, value)}
      />
    </Card >
  );
}

MovieItem.defaultProps = {
  movie: {},
  setMyRatedMovies: () => { }
};

MovieItem.propTypes = {
  movie: PropTypes.objectOf(PropTypes.any),
  setMyRatedMovies: PropTypes.func
};


export default MovieItem;